const body=document.body,modal=document.querySelector(".modal"),btnRegister=document.getElementById("buttonOpenRegister"),btnCloseRegister=document.getElementById("buttonCloseRegister");btnRegister.addEventListener("click",(function(){body.classList.add("body--active"),modal.classList.add("modal--active")})),btnCloseRegister.addEventListener("click",(function(){body.classList.remove("body--active"),modal.classList.remove("modal--active"),modal.scrollTop=0}));